import React, { useEffect, useRef } from 'react'
import {Container, Card, FormControl, Button} from 'react-bootstrap';

function Pay() {
  const clickHandler = ()=> {
    alert("Your tips have been sent.");
  }
  const paypal = useRef();

  useEffect(()=> {
    window.paypal.Buttons({
      createOrder: (data, actions, err) => {
        return actions.order.create({
          intent: "CAPTURE",
          purchase_units: [
            {
              description: "Tips",
              amount: {
                currency_code: "USD",
                value: 2.00
              }
            }
          ]
        })
      },
      onApprove: async(data, actions)  => {
        const order = await actions.order.capture();
        console.log(order);
      }
    }).render(paypal.current)
  })

  return (
    <Container className="text-center">

    <div>Buttface</div>
    <FormControl></FormControl><Button className="btn btn-success" onClick ={clickHandler}>Send Tips</Button>


    <div ref = {paypal}></div>


    </Container>

  )
}

export default Pay
